'use strict';const {PrismaClient}=require("./app/generated/prisma"),prisma=new PrismaClient;
async function verifyUser(c){try{console.log(`Verifying user: ${c}`);const a=await prisma.user.findUnique({where:{email:c}});if(a)if(console.log("User found:",{id:a.id,email:a.email,emailVerified:a.emailVerified}),a.emailVerified)console.log("User is already verified");else{var b=await prisma.user.update({where:{id:a.id},data:{emailVerified:!0,emailVerifiedAt:new Date}});console.log("User verified successfully:",{id:b.id,email:b.email,emailVerified:b.emailVerified})}else console.log("User not found")}catch(a){console.error("Error:",
a)}finally{await prisma.$disconnect()}}verifyUser("bruno@test.test");